mapper.select:
select count(1)
from item
where ItemNumber=#{itemNumber,jdbcType=VARCHAR}

select

from item


    and id = #{id,jdbcType=BIGINT}


    and ItemNumber=#{itemNumber,jdbcType=VARCHAR}


    and Description like #{description,jdbcType=VARCHAR}


    and `Status`=#{status,jdbcType=TINYINT}


    and Createdtime  >=  #{createStartTime,jdbcType=TIMESTAMP}


    and Createdtime  <=  #{createEndTime,jdbcType=TIMESTAMP}


    and EditTime  >=  #{editStartTime,jdbcType=TIMESTAMP}


    and EditTime  <=  #{editEndTime,jdbcType=TIMESTAMP}


    and editUser=#{editUser,jdbcType=VARCHAR}

  order by EditTime desc

    limit #{page,jdbcType=INTEGER} ,#{limit,jdbcType=INTEGER}

select count(1)
from item


    and id <> #{notId,jdbcType=BIGINT}


    and ItemNumber=#{itemNumber,jdbcType=VARCHAR}
